import React, {Component} from "react";
import QuizQuestion from "./QuizQuestion.js"


//Get quiz question number, question text, answer options and correct answer from json file
let quizData = require('./quiz_data.json')


//make data available in state for render
class Quiz extends Component {
    constructor(props){
        super(props)
        this.state = {
            quiz_position:1,
            quiz_question: [],
            currentIndex: 0,
            answer_options: [],
            answer: null

        }  
    }

    loadQuiz = () => {
        const {currentIndex} = this.state;
        this.setState( () =>{
            return {quiz_question: quizData[currentIndex].quiz_question,
                    answer_options: quizData[currentIndex].answer_options,
                    answer: quizData[currentIndex].answer
        }})
    }

    componentDidMount(){
        this.loadQuiz();
    }

    //render quiz question on html page
    render(){
        const {quiz_question, answer_options, answer} = this.state
        return <div className="QuizQuestion" >
            <h2>{quiz_question}</h2>


        </div>
    }
}

export default Quiz
